import React, { createContext, useContext, useState, useEffect } from 'react';
import { auth } from '../firebaseConfig'; // Asegúrate de importar Firebase correctamente
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const useAuth = () => {
  return useContext(AuthContext);
};

export const AuthProvider = ({ children }) => {
  const [userData, setUserData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(user => {
      if (user) {
        // Puedes aquí obtener datos adicionales del usuario si es necesario
        setUserData({
          email: user.email,
          rol: 'user', // o 'admin' dependiendo de tu lógica
        });
      } else {
        setUserData(null);
      }
    });

    return unsubscribe;
  }, []);

  const logout = () => {
    auth.signOut().then(() => {
      setUserData(null);
      navigate('/login');
    });
  };

  return (
    <AuthContext.Provider value={{ userData, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
