// src/components/Login.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { loginUser } from '../services/authService';  // Importa la función loginUser
import { Helmet } from 'react-helmet';

  const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const isEmailValid = email.includes('@');
  const isPasswordValid = password.length >= 6;
  const isFormValid = isEmailValid && isPasswordValid;

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);  // Activar el estado de carga

    try {
      const user = await loginUser(email, password);  // Llamar a loginUser
      console.log('Usuario autenticado:', user);
      navigate("/dashboard");  // Redirigir a dashboard después del login exitoso
    } catch (err) {
      console.error("ERROR:", err.code, err.message);
      if (err.code === "auth/user-not-found") {
        setError("No se encuentra un usuario con ese correo.");
      } else if (err.code === "auth/wrong-password") {
        setError("Contraseña incorrecta.");
      } else {
        setError("Error al iniciar sesión. Verifica tus datos.");
      }
    } finally {
      setLoading(false);  // Desactivar el estado de carga
    }
  };

  return (
    <>
      <Helmet>
        {/* Enlaza el CSS y el main.js desde la carpeta animated-login que ahora está en public */}
        <link rel="stylesheet" href="/animated-login/assets/css/styles.css" />
        <script src="/animated-login/assets/js/main.js" defer></script>
      </Helmet>
    </>
  );
};

export default Login;
