import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { registerUser } from '../services/authService'; // Asegúrate de importar la función desde authService
import { Helmet } from '../services/authService';

export const Register = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [rol, setRol] = useState('user');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const isEmailValid = email.includes('@');
  const isPasswordValid = password.length >= 6;
  const isFormValid = isEmailValid && isPasswordValid;

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);  // Activar el estado de carga

    try {
      await registerUser(email, password, rol); // Llamar a registerUser
      navigate("/dashboard");  // Redirigir después del registro exitoso
    } catch (err) {
      console.error("ERROR:", err.code, err.message);
      if (err.code === "auth/email-already-in-use") {
        setError("El correo ya está registrado.");
      } else if (err.code === "auth/weak-password") {
        setError("La contraseña debe tener al menos 6 caracteres.");
      } else if (err.code === "auth/invalid-email") {
        setError("El correo no tiene un formato válido.");
      } else {
        setError("Error al registrar usuario. Verifica tus datos.");
      }
    } finally {
      setLoading(false); // Desactivar el estado de carga
    }
  };

  return (
    <>
      <Helmet>
        <link rel="stylesheet" href="/animated-login/assets/css/styles.css" />
        <script src="/animated-login/assets/js/main.js" defer></script>
      </Helmet>

      <div className="container d-flex justify-content-center align-items-center vh-100">
        <div className="card shadow p-4" style={{ maxWidth: '400px', width: '100%' }}>
          <h2 className="text-center mb-4">Crear Cuenta</h2>
          {/* ...formulario de registro */}
          <form onSubmit={handleRegister}>
            {/* ...campos y botones */}
          </form>
          <div className="text-center mt-3">
            <a href="/animated-login/register.html">Ver versión animada</a>
          </div>
        </div>
      </div>
    </>
  );
};

export default Register;
