// src/components/Login.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { loginUser } from '../services/authService';  // Importa la función loginUser

export const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const isEmailValid = email.includes('@');
  const isPasswordValid = password.length >= 6;
  const isFormValid = isEmailValid && isPasswordValid;

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);  // Activar el estado de carga

    try {
      const user = await loginUser(email, password);  // Llamar a loginUser
      console.log('Usuario autenticado:', user);
      navigate("/dashboard");  // Redirigir a dashboard después del login exitoso
    } catch (err) {
      console.error("ERROR:", err.code, err.message);
      if (err.code === "auth/user-not-found") {
        setError("No se encuentra un usuario con ese correo.");
      } else if (err.code === "auth/wrong-password") {
        setError("Contraseña incorrecta.");
      } else {
        setError("Error al iniciar sesión. Verifica tus datos.");
      }
    } finally {
      setLoading(false);  // Desactivar el estado de carga
    }
  };

  return (
    <div className="container d-flex justify-content-center align-items-center vh-100">
      <div className="card shadow p-4" style={{ maxWidth: '400px', width: '100%' }}>
        <h2 className="text-center mb-4">Iniciar Sesión</h2>

        {error && <div className="alert alert-danger">{error}</div>}

        <form onSubmit={handleLogin}>
          <div className="mb-3">
            <label className="form-label">Correo electrónico</label>
            <input
              type="email"
              className={`form-control ${email && !isEmailValid ? 'is-invalid' : ''}`}
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            {!isEmailValid && email && <div className="invalid-feedback">Correo inválido</div>}
          </div>

          <div className="mb-3">
            <label className="form-label">Contraseña</label>
            <input
              type="password"
              className={`form-control ${password && !isPasswordValid ? 'is-invalid' : ''}`}
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            {!isPasswordValid && password && (
              <div className="invalid-feedback">Mínimo 6 caracteres</div>
            )}
          </div>

          <button
            type="submit"
            className="btn btn-success w-100"
            disabled={!isFormValid || loading}
          >
            {loading ? (
              <>
                <span className="spinner-border spinner-border-sm me-2" role="status" />
                Iniciando sesión...
              </>
            ) : (
              'Iniciar sesión'
            )}
          </button>
        </form>

        <div className="text-center mt-3">
          <small>¿No tienes cuenta? <a href="/register">Regístrate</a></small>
        </div>
      </div>
    </div>
  );
};

export default Login;
