import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { registerUser } from '../services/authService'; // Asegúrate de importar la función desde authService


export const Register = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [rol, setRol] = useState('user');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const isEmailValid = email.includes('@');
  const isPasswordValid = password.length >= 6;
  const isFormValid = isEmailValid && isPasswordValid;

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);  // Activar el estado de carga

    try {
      await registerUser(email, password, rol); // Llamar a registerUser
      navigate("/dashboard");  // Redirigir después del registro exitoso
    } catch (err) {
      console.error("ERROR:", err.code, err.message);
      if (err.code === "auth/email-already-in-use") {
        setError("El correo ya está registrado.");
      } else if (err.code === "auth/weak-password") {
        setError("La contraseña debe tener al menos 6 caracteres.");
      } else if (err.code === "auth/invalid-email") {
        setError("El correo no tiene un formato válido.");
      } else {
        setError("Error al registrar usuario. Verifica tus datos.");
      }
    } finally {
      setLoading(false); // Desactivar el estado de carga
    }
  };

  return (
    <body>
      <div class="login">
        <img src="assets/img/royal-albert-hall-e1638556433427.png" alt="login image" class="login__img" />

         <form action="" class="login__form">
            <h1 class="login__title">Register</h1>

            <div class="login__content">
               <div class="login__box">
                  <i class="ri-user-3-line login__icon"></i>

                  <div class="login__box-input">
                     <input type="email" required class="login__input" id="login-email" placeholder=" " />
                     <label for="login-email" class="login__label">Email</label>
                  </div>
               </div>

               <div class="login__box">
                  <i class="ri-lock-2-line login__icon"></i>

                  <div class="login__box-input">
                     <input type="password" required class="login__input" id="login-pass" placeholder=" " />
                     <label for="login-pass" class="login__label">Password</label>
                     <i class="ri-eye-off-line login__eye" id="login-eye"></i>
                  </div>
               </div>
            </div>

        

            <button type="submit" class="login__button">Login</button>

            <p class="login__register">
               Already registered? <a href="login.html">Login</a>
            </p>
         </form>
      </div>
    </body>
  );
};

export default Register;
