// components/ProtectedRoute.js
import { Navigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import { AuthContext } from '../contexts/AuthContext';


const ProtectedRoute = ({ children, requiredRole }) => {
  const { user, loading } = useContext(AuthContext);

  if (loading) {
    return <div>Loading...</div>;  // Puedes mostrar un spinner o algo mientras se carga el estado de autenticación
  }

  if (!user) {
    return <Navigate to="/login" />;  // Redirige a login si el usuario no está autenticado
  }

  if (requiredRole && user.role !== requiredRole) {
    return <Navigate to="/dashboard" />;  // Redirige si el usuario no tiene el rol necesario
  }

  return children;  // Si el usuario está autenticado y tiene el rol requerido, renderiza el componente
};

export default ProtectedRoute;
